<?phpclass Selling extends CI_Controller{         function __construct()    {         parent :: __construct();         $this -> load -> helper('html');		 $this->load->helper('url');		 $this->load->database();		 $this->load->library('pagination');		 $this->load->model('sellingModel');		 $this->load->library('form_validation');		 $this->load->helper('date');    }         function _makeHeader(&$data)    {         echo doctype('html5');        $data['keywords'] = 'This, is, keywords';        $data['description'] = 'This is description';        $data['robots'] = 'This is robots part';		$data['javascript'] = array(//base_url().'resource/selling/jquery.easing.1.3.js',									// base_url().'resource/selling/jquery.accordion.js',									// base_url().'resource/date.js',									base_url().'resource/datepicker/js/datepicker.js');		$data['css'] = array(base_url().'resource/common.css',							base_url().'resource/footer/footer.css',							// base_url().'resource/datePicker.css',							base_url().'resource/datepicker/css/datepicker.css',							base_url().'resource/selling/selling.css');		$data['username'] = $this->session->userdata('username');		$data['login'] = $this->session->userdata('login');    }         function index()    {          $this->page(0,0,0);		  //print_r($data['selling']);	    }		function page($collageNum=0, $subjectNum=0, $pageNum = 0)	{		 $data = array();         $this -> _makeHeader($data);		 		 $this ->sellingModel-> queryData($data,$collageNum,$subjectNum,$pageNum);		 $config['base_url'] = site_url().'/selling/page';		 $config['total_rows'] = $data['pages'];		 $config['per_page'] = '10'; 		 $this->pagination->initialize($config);		 $data['pages'] = $this->pagination->create_links();		 		 $data['subjects'] = $this ->sellingModel-> otherSubject($subjectNum, $collageNum);		 $data['collages'] = $this ->sellingModel-> otherCollage($collageNum);		          $this -> load -> view('header', $data);         $this -> load -> view('selling');		 $this -> load -> view('footer');	} 		function single($id=1,$action='show')	{		 $data = array();         $this -> _makeHeader($data);		 		 $hasData = $this ->sellingModel-> querySelling($data, $id);		 if(!$hasData){			redirect('missing');  			return;		 }		 if($action=='close'){			$book_owner = $this ->sellingModel->whosBook($id);			if($book_owner == $this->session->userdata('userid')){				$this ->sellingModel->closeBook($id);				$data['msgtitle'] = '图书已成功关闭';				$data['msgcontent'] = '图书已成功关闭，将不会再出现在最新售出栏目当中。';				$this -> load -> view('header', $data);			    $this -> load -> view('message-template');			    $this -> load -> view('footer');				return;			}		 }else{			 $data['subjects'] = $this ->sellingModel-> sameSubjectBook($id);			 $data['collages'] = $this ->sellingModel-> sameCollageBook($id);			 $this -> load -> view('header', $data);			 $this -> load -> view('selling_single');			 $this -> load -> view('footer');			 return;		 }	} 		function create()	{		 $data = array();         $this -> _makeHeader($data);		 //$data['datestart'] = date("Y-F-d");//unix_to_human(time());		 //$data['dateend'] = unix_to_human(time());		 $this -> load -> view('header', $data);		 $this -> load -> view('selling_create');		 $this -> load -> view('footer');		}}?>