<?phpclass Selling extends CI_Controller{         function __construct()    {         parent :: __construct();         $this -> load -> helper('html');		 $this->load->helper('url');		 $this->load->database();		 $this->load->library('pagination');		 $this->load->model('Sellingmodel');    }         function _makeHeader(&$data)    {         echo doctype('html5');        $data['keywords'] = 'This, is, keywords';        $data['description'] = 'This is description';        $data['robots'] = 'This is robots part';		$data['javascript'] = array(base_url().'resource/selling/jquery.easing.1.3.js',base_url().'resource/selling/jquery.accordion.js');		$data['css'] = array(base_url().'resource/common.css',base_url().'resource/footer/footer.css',base_url().'resource/selling/selling.css');		$data['username'] = $this->session->userdata('username');		$data['login'] = $this->session->userdata('login');    }         function index()    {          $this->page(2,1,0);		  //print_r($data['selling']);	    }		function page($collageNum=2, $subjectNum=0, $pageNum = 0)	{		 $data = array();         $this -> _makeHeader($data);		 $config['base_url'] = site_url().'/selling/page';		 $config['total_rows'] = $this->Sellingmodel-> queryNum();		 $config['per_page'] = '10';          $this->pagination->initialize($config); 		 $data['pages'] = $this->pagination->create_links();		 $this ->Sellingmodel-> queryData($data, $pageNum,$collageNum, $subjectNum);		 $data['subjects'] = $this ->Sellingmodel-> otherSubject($subjectNum);		 $data['collages'] = $this ->Sellingmodel-> otherCollage($collageNum);         $this -> load -> view('header', $data);         $this -> load -> view('selling');		 $this -> load -> view('footer');	} }?>